import React, { useEffect, useState } from 'react';
import api from '../api';

const Users = () => {
  const [users, setUsers] = useState(api.users.fetchAll());

  class DeleteButton extends React.Component {
    handleDelete = () => {
      const { onDelete, index } = this.props;
      onDelete(index);
    };

    render() {
      return <button onClick={this.handleDelete}>Удалить</button>;
    }
  }

  return (
    <>
      <table class="table">
        <thead>
          <tr>
            <th scope="col">Имя</th>
            <th scope="col">Качество</th>
            <th scope="col">Професия</th>
            <th scope="col">Встретился раз</th>
            <th scope="col">Оценка</th>
            <th scope="col">Удалить</th>
          </tr>
        </thead>
        <tbody>
          {users.map((users, index) => (
            <tr>
              <td key={users.id}>{users.name}</td>
              <td key={users.id}>{users.n}</td>
              <td key={users.id}>{users.professions}</td>
              <td key={users.id}>{users.completedMeetings}</td>
              <td key={users.id}>{users.rate}</td>
              <td>
                <DeleteButton index={index} onDelete={this.handleDelete} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Users;
