import React, { useEffect, useState } from 'react';
import api from '../api';

const Users = () => {
  const [users, setUsers] = useState(api.users.fetchAll());

  let count = document.querySelectorAll('table tr').length - 1;
  function updateBadge() {
    count = document.querySelectorAll('table tr').length - 1;
    const badge = document.querySelector('.badge');
    if (count > 0) {
      badge.textContent = `${count} человек тусанет сегодня с тобой`;
    } else {
      badge.textContent = 'Никто не тусанет сегодня с тобой';
      document.querySelector('table').remove();
    }
  }

  const handleDelete = (id) => {
    console.log(id);
    setUsers((prevState) => prevState.filter((users) => users !== id));
  };

  return (
    <>
      <h1>
        <span class="badge bg-secondary">``</span>
      </h1>
      <table class="table">
        <thead>
          <tr>
            <th scope="col">Имя</th>
            <th scope="col">Качество</th>
            <th scope="col">Професия</th>
            <th scope="col">Встретился раз</th>
            <th scope="col">Оценка</th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          {users.map((users) => (
            <tr>
              <td key={users.id}>{users.name}</td>
              <td key={users.id}>{users.n}</td>
              <td key={users.id}>{users.professions}</td>
              <td key={users.id}>{users.completedMeetings}</td>
              <td key={users.id}>{users.rate}</td>
              <td>
                <button
                  className="btn btn-danger position-relative"
                  onClick={() => handleDelete(users)}>
                  Удалить
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Users;
