import React, { useState } from 'react';
import api from '../api';

const Users = () => {
  const [users, setUsers] = useState(api.users.fetchAll());

  const handleDelete = (id) => {
    setUsers((prevState) => prevState.filter((users) => users !== id));
  };

  const rowCount = users.length;
  const deleteTable = document.querySelector('.table');
  const handleBage = () => {
    return rowCount === 0
      ? 'Никто сегодня с тобой не тусует' && deleteTable.filter()
      : `${rowCount} человек сегодня с тобой тусует`;
  };
  const getBageClasses = () => {
    return rowCount === 0 ? 'badge bg-secondary' : 'badge bg-primary';
  };

  return (
    <>
      <h1>
        <span className={getBageClasses()}>{handleBage()}</span>
      </h1>
      <table class="table">
        <thead>
          <tr>
            <th scope="col">Имя</th>
            <th scope="col">Качество</th>
            <th scope="col">Професия</th>
            <th scope="col">Встретился раз</th>
            <th scope="col">Оценка</th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          {users.map((users) => (
            <tr>
              <td key={users.id}>{users.name}</td>
              <td key={users.id}>{users.n}</td>
              <td key={users.id}>{users.professions}</td>
              <td key={users.id}>{users.completedMeetings}</td>
              <td key={users.id}>{users.rate}</td>
              <td>
                <button
                  className="btn btn-danger position-relative"
                  onClick={() => handleDelete(users)}>
                  Удалить
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Users;
