import React, { useEffect, useState } from 'react';
import api from '../api';

const Users = () => {
  const [users, setUsers] = useState(api.users.fetchAll());

  const handleDelete = (id) => {
    const newUsers = users.filter((users) => users.id !== id);
    setUsers(newUsers);
  };

  return (
    <>
      <table class="table">
        <thead>
          <tr>
            <th scope="col">Имя</th>
            <th scope="col">Качество</th>
            <th scope="col">Професия</th>
            <th scope="col">Встретился раз</th>
            <th scope="col">Оценка</th>
            <th scope="col">Удалить</th>
          </tr>
        </thead>
        <tbody>
          {users.map((users, professions) => (
            <tr>
              <td key={users.id}>{users.name}</td>
              <td key={users.id}>{users.n}</td>
              <td key={users.id}>{professions}</td>
              <td key={users.id}>{users.completedMeetings}</td>
              <td key={users.id}>{users.rate}</td>
              <td>
                <button onClick={() => handleDelete(users.id)}>Удалить</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Users;
